import React from 'react'

import { FcLike } from 'react-icons/fc';
import { toast } from 'react-toastify';

const Card = (props) => {
  let course=props.course;
  let likedCourses=props.likedCourses;
  let setLikedCourses=props.setLikedCourses;
  function clickHandler(){
    //logic
    if(likedCourses.includes(course.id)){
      //pehle se like hua pada try {
        setLikedCourses((prev)=>prev.filter((cid!==course.id)));
        toast.warning("Like removed");
    }
  }
  return (
    <div className="w-[300px] bg-bgDark bg-opacity-90 rounded-md overflow-hidden ">
      <div className="relative">
        <img src={course.image.url }/>

        <div className="w-[40px] h-[40px] bg-white rounded-full absolute right-2 bottom-3
        grid place-items-center">
        <button onClick={clickHandler}>
             <FcLike fontSize="1.75rem"/>
        </button>
      </div>
      </div>


      <div className="p-4">
        <p className="text-white font-semibold text-lg leading-4">{course.title}</p>
        <p className="mt-2 text-white">{course.description}</p>
      </div>

    </div>
  )
}

export default Card;
